mapper.select:
SELECT alarm_idx, alarm_type, title, alarm_date, alarm_time, content,
       link, is_routing, is_read, users_idx,house_type
FROM alarm
WHERE alarm_idx = #{alarmIdx}
  AND users_idx = #{usersIdx}

mapper.update:
UPDATE alarm
SET is_read = TRUE
WHERE alarm_idx = #{alarmIdx}
  AND users_idx = #{usersIdx}
  AND is_read = FALSE

mapper.delete:
DELETE FROM alarm
WHERE alarm_idx = #{alarmIdx}
  AND users_idx = #{usersIdx}
